<?phpnamespace app\controllers\actions;use app\base\BaseActions;use app\models\Activity;class ActivityCreateAction extends BaseActions {    public $text;    public function run() {        $model = new Activity;        if (\Yii::$app->request->isPost) {            $model->load(\Yii::$app->request->post());        }        return $this->controller->render('create', [            'text' => $this->text,            'model' => $model        ]);    }}