<?phpnamespace app\models;use yii\base\Model;class Activity extends Model {    public $title;    public $description;    public $dateStart;    public $dateFinish;    public $isBlocked;    public $isRepeat;    public $remind;    public function rules() {        return [            [                'title',                'string',                'min' => 5,                'max' => 10,            ],            [                [                    'isBlocked',                    'isRepeat'                ],                'boolean'            ],            [                [                    'dateStart',                    'dateFinish',                    'description',                    'title',                    'remind'                ],                'required'            ],            [                'dateFinish',                'compare',                'compareAttribute' => 'dateStart',                'operator' => '>='            ],        ];    }    public function attributeLabels() {        return [            'title' => 'Заголовок обытия',            'description' => 'Описание',            'isBlocked' => 'Блокирующее',            'dateStart' => 'Начало',            'dateFinish' => 'Конец',            'isRepeat' => 'Повторяющееся',            'remind' => 'Напомнить о событии'        ];    }}